import { useEffect, useState } from 'react';
import PokeAPI from '../api/PokeAPI';
import PokemonList from '../components/PokemonList';

function CreateTeamPage(props){

  // states
  const [pokemonData, setPokemonData] = useState([])
  const [poketeam, setPoketeam] = useState([])
  //event handler
  const loadPokemon = async () => {
    const filteredPokemonData = await PokeAPI.getAllPokemon()
      // set a data variable, wait for load pokemon to load...then call API here.
      // console.log(filteredPokemonData)
      // console.log(data)
      //if data within API call exists then return the values of that data and set it as the current value.
       setPokemonData(filteredPokemonData)
  }

// effects
  useEffect(() => {
    loadPokemon()
  }, [props.username])


  return (
    <div>
      {/* from here to next component can be another component almost the same as pokemon list but with a remove button instead
      for the remove button try to copy sendtoteam logic but in reverse
      if you make a component you will need to pass poketeam as a prop to the component */}
      <h2> Your Team: </h2>
      <ul>
        {
          poketeam.map(pokemon => (
            <div key={pokemon.name}>
              {
                //this ternary operator is here because i can't get rid of the first empty element in array
                pokemon.name && 
                <div>
                  <li>
                    {pokemon.name}
                  </li>
                  <img src={pokemon.sprite}></img>
                </div>  
              }
            </div>
          ))
        }
      </ul>
      <hr />
      <h3>Choose your Pokemon!</h3>
      <PokemonList poketeam={poketeam} setPoketeam={setPoketeam} pokemonData={ pokemonData } />
    </div>
  );
}
export default CreateTeamPage;
