import { useNavigate } from 'react-router-dom'
import { Link } from 'react-router-dom'
import PokeAPI from '../api/PokeAPI'
function HomePage(props){

  const navigate = useNavigate()
  // helper
  const doLogout = async () => {
    const data = await PokeAPI.logout()
    if (data) {
      props.setUsername("")
      navigate("/")
    }
  }

  //render helper
  const renderAuthItems = () => {
    if (props.username === "") {
      return (
      <div>
        <button><Link to="signup">Sign Up</Link></button>
        <button><Link to="login">Login to Account</Link></button>
      </div>
      )
    }

    return(
      <button><Link to="/" onClick={ doLogout } >Log Out</Link></button>
    )
  }

  return(
    <div>
      <h2>Pokemon Team Creator</h2>
      <p>Sign up or sign in to start making your own Pokemon Team!</p>
      <h4>Image placeholder</h4>
      { renderAuthItems() }
      {/* <button><Link to="signup">Sign Up</Link></button>
      <button><Link to="login">Login to Account</Link></button> */}
    </div>
  );
}

export default HomePage;
