import { useNavigate } from 'react-router-dom'
import { Link } from 'react-router-dom'
import PokeAPI from '../api/PokeAPI'
import classes from "./HomePage.module.css"
function HomePage(props){

  const renderAuthItems = () => {
    if (props.username === "") {
      return (
      <>
        <button><Link to="signup">Sign Up</Link></button>
        <button><Link to="login">Login to Account</Link></button>
      </>
      )
    }
  }

  return(
    <div className={classes.home}>
      <h2>Pokemon Team Creator</h2>
      <p><a href="login">Login</a> or <a href="signup">sign up</a> to start making your own Pokemon Team!</p>
      <img src="https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/official-artwork/700.png" className={classes.homeimg}></img>
      <br/>
      { renderAuthItems() }
    </div>    
  );
}

export default HomePage;
