import PokeAPI from "../api/PokeAPI"
import { useState, useEffect } from 'react'

const PokemonList = (props) => {
  const [pokeTeam, updatePoketeam] = useState(null)
  const {pokemonData} = props

  const sendToTeam = (name, sprite) => {
    // console.log(name, sprite)
    const teamData = [] //empty list, gonna push the values we get into this list.
  // take name and sprite and put it into state (DONE? grabs from pokemonData map function, places them as parameters in this function)
  // map over the state? xxxx
    teamData.push(name) //adding this to list
    teamData.push(sprite)
    updatePoketeam(teamData) //setting init state as teamData list
    console.log(teamData)
  // render at top of screen
  }

  useEffect(() => {
    updatePoketeam()
  }, [pokeTeam])

  return (
    <div>
      {pokeTeam}
      <ul>
        {props.pokemonData?.map((pokemon) => (
           <div key={pokemon.namePokemon}>
           <li key={pokemon.namePokemon}>
             {pokemon.namePokemon}
           </li>
           <img src={pokemon.spritePokemon}></img>
           <div>{pokemon.descriptionPokemon}</div>
           <button onClick={() => sendToTeam(pokemon.namePokemon, pokemon.spritePokemon)}>Add to Team</button>
         </div>
        ))}
      </ul>
    </div>
  )
}

export default PokemonList;
