import PokeAPI from "../api/PokeAPI"
import { useState, useEffect } from 'react'
import classes from "./PokemonList.module.css"

const PokemonList = (props) => {
  // const [pokeTeam, updatePoketeam] = useState(null)
  // console.log(props)
  // const {pokemonData} = props

  const sendToTeam = (name, sprite) => {
    const updatePoketeam = [
        //copy current poketeam state
      ...props.poketeam, 
      {
        // adding new pokemon name
        name: name,
        // with their sprite
        sprite: sprite
      }
    ]
    // updating state
    props.setPoketeam(updatePoketeam) 
  }

  // console.log(props.pokeTeam)
  useEffect(() => {
    sendToTeam()
  }, [])

  return (
    <div className={classes.row}>
      <ul className={classes.column}>
        {props.pokemonData?.map((pokemon) => (
           <div key={pokemon.namePokemon} className={classes.card}>
           <ul key={pokemon.namePokemon}>
             {pokemon.namePokemon}
           </ul>
           <img src={pokemon.spritePokemon}></img>
           <div>{pokemon.descriptionPokemon}</div>
           <button onClick={() => sendToTeam(pokemon.namePokemon, pokemon.spritePokemon)}>Add to Team</button>
         </div>
        ))}
      </ul>
    </div>
  )
}

export default PokemonList;
